import telebot
from config import token
from telebot import types

def telegram_bot(token):
    bot = telebot.TeleBot(token)

    @bot.message_handler(commands=["start"])
    def welcome(message):

        # keyboard
        markup = types.ReplyKeyboardMarkup(one_time_keyboard=True)
        item1 = types.KeyboardButton("–¶ –ª–∞–¥–Ω–æ –≥–æ–≤–æ—Ä–∏ —É–∂–µ")
        item2 = types.KeyboardButton("–ú–¥–∞ —Ç—Ä–µ—à, –ø–æ—à–µ–ª –Ω–∞@%$")
        item3 = types.KeyboardButton("–†–∞—Å—Å–∫–∞–∂–∏ –º–Ω–µ –æ –Ω–µ–º!")
        markup.add(item1, item2, item3)

        bot.send_message(message.chat.id, "–ü—Ä–∏–≤–µ—Ç {0.first_name}! {1.first_name} –±–æ—Ç,c–æ–∑–¥–∞–Ω–Ω—ã–π —á—Ç–æ –±—ã –ø–µ—Ä–µ–¥–∞—Ç—å –ø–æ—Å–ª–∞–Ω–∏–µ –æ–¥–Ω–æ–≥–æ —á–µ–ª–æ–≤–µ—á–∫–∞üòä. –ü–æ –ø—Ä–∞–≤–¥–µ –≥–æ–≤–æ—Ä—è –æ–Ω –º–µ–Ω—è –∂—É—Ç–∫–æ –±–µ—Å–∏—Ç, –Ω–æ –ø—Ä–∏–∫–∞–∑—ã –Ω–µ –æ–±—Å—É–∂–¥–∞—é—Ç—Å—èüòû".
        format(message.from_user, bot.get_me()), parse_mode="html", reply_markup=markup)
        sti = open("stik/AnimatedSticker.tgs", "rb")   #–ø–æ–Ω—è—Ç—å —á—Ç–æ —ç—Ç–æ –∏ –∫–∞–∫ —Ä–∞–±–æ—Ç–∞–µ—Ç
        bot.send_sticker(message.chat.id, sti)

    @bot.message_handler(content_types=["text"])
    def You_are_my_kit_bot(message):
        if message.chat.type == "private":
            if message.text == "–¶ –ª–∞–¥–Ω–æ –≥–æ–≤–æ—Ä–∏ —É–∂–µ":
                bot.send_message(message.chat.id,"–í–æ–∑–º–æ–∂–Ω–æ —è –¥—É—à–Ω–∏–ª–∞ –∫–∞–∫ –∏ –º–æ–π —Å–æ–∑–¥–∞—Ç–µ–ª—å, –Ω–æ –≤ –ø–æ—Å–ª–µ–¥–Ω–µ –≤—Ä–µ–º—è –º–µ–∂–¥—É –≤–∞–º–∏ –±—ã–ª–æ –º–Ω–æ–≥–æ –∫–æ–Ω—Ñ–ª–∏–∫—Ç–æ–≤, –∫–æ—Ç–æ—Ä—ã–µ –∫–∞–∫ –º–Ω–µ –∫–∞–∂–µ—Ç—Å—è –Ω–µ —Å—Ç–æ—è—Ç —Ç–æ–≥–æ. –ö —Å–æ–∂–∞–ª–µ–Ω–∏—é, –∞ –º–æ–∂–µ—Ç –∏ –∫ —Å—á–∞—Å—Ç—å—é –æ–Ω –Ω–µ —Ä–æ–±–æ—Ç –∏ –Ω–µ –º–æ–∂–µ—Ç –¥–µ–ª–∞—Ç—å –≤—Å–µ —Ç–∞–∫ –∫–∞–∫ —Ö–æ—Ç–µ–ª–æ—Å—å –±—ã —Ç–µ–±–µ. –û–Ω —á–µ–ª–æ–≤–µ–∫, –æ–Ω —Ç–æ–∂–µ –∏—Å–ø—ã—Ç—ã–≤–∞–µ—Ç —ç–º–æ—Ü–∏–∏, –æ–Ω —Ç–æ–∂–µ –æ—à–∏–±–∞–µ—Ç—Å—è, –æ–Ω –ø—ã—Ç–∞–µ—Ç—Å—è –±—ã—Ç—å –ø–∞—Ä–Ω–µ–º –∫–æ—Ç–æ—Ä–æ–≥–æ —Ç—ã –∑–∞—Å–ª—É–∂–∏–≤–∞–µ—à—å, –Ω–æ –ø–æ-–º–æ–µ–º—É —É –Ω–µ–≥–æ –Ω–µ –æ—á–µ–Ω—å —Ö–æ—Ä–æ—à–æ –ø–æ–ª—É—á–∞–µ—Ç—Å—è.\n –í –ø–æ—Å–ª–µ–¥–Ω–µ–µ –≤—Ä–µ–º—è —É –≤–∞—Å –∏ –ø—Ä–∞–≤–¥–∞ –æ—á–µ–Ω—å –º–Ω–æ–≥–æ –∫–æ–Ω—Ñ–ª–∏–∫—Ç–æ–≤, —è –≤–∞—Å –Ω–µ –æ—Å–æ–±–æ –ø–æ–Ω–∏–º–∞—é, –≤–µ–¥—å –æ–Ω–∏ –≤—ã–≥–ª—è–¥—è—Ç –æ—á–µ–Ω—å —Å—Ç—Ä–∞–Ω–Ω—ã–º–∏, –Ω–æ –∫–∞–∫ –º–Ω–µ –∫–∞–∂–µ—Ç—Å—è –≤–∞–º –Ω—É–∂–Ω–æ –Ω–∞—Ö–æ–¥–∏—Ç—å —Ä–µ—à–µ–Ω–∏—è –ø—Ä–æ–±–ª–µ–º—ã –∞ –Ω–µ —Å—Å–æ—Ä–∏—Ç—å—Å—è –∏–∑-–∑–∞ –Ω–∏—Ö. –ù–µ–∫–æ—Ç–æ—Ä—ã–µ –ø—Ä–æ–±–ª–µ–º—ã –≤–æ–æ–±—â–µ –Ω–µ —Å—Ç–æ—è—Ç –≤–Ω–∏–º–∞–Ω–∏—è, —ç—Ç–æ –º–∞–ª–µ–Ω—å–∫–∏–µ —Å–∏—Ç—É–∞—Ü–∏–∏, –≤–æ–∑–º–æ–∂–Ω–æ –Ω–µ –æ—á–µ–Ω—å –ø—Ä–∏—è—Ç–Ω—ã–µ —Ç–µ–±–µ, –Ω–æ –∫–∞–∫ –º–Ω–µ –∫–∞–∂–µ—Ç—Å—è –Ω–µ —Å—Ç–æ—è—Ç —Ç–≤–æ–∏—Ö –∑–∞–≥–æ–Ω–æ–≤, –Ω–µ—Ä–≤–Ω—ã—Ö –∫–ª–µ—Ç–æ–∫ –∏ —Å–∏–ª.\n –Ø –ø–æ–Ω–∏–º–∞—é –∫–∞–∫ —Ç–µ–±–µ —Ç—è–∂–µ–ª–æ —Å–µ–π—á–∞—Å(—Ö–æ—Ç—è —è –±–æ—Ç, –Ω–æ –≤—Å–µ –∂–µ),—É —Ç–µ–±—è –∫—É—á—É –ø—Ä–æ–±–ª–µ–º, –Ω–∞—á–∏–Ω–∞—è –æ—Ç —Ç–≤–æ–µ–≥–æ –º–µ–Ω—Ç–∞–ª—å–Ω–æ–≥–æ –∏ —Ñ–∏–∑–∏—á–µ—Å–∫–æ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è, –∑–∞–∫–∞–Ω—á–∏–≤–∞—è —Å—Ä–∞–Ω–æ–π —à–∫–æ–ª–æ–π, —ç–∫–∑–∞–º–µ–Ω–∞–º–∏ –∏ –ø—Ä–æ—á–µ–π —Ñ–∏–≥–Ω–µ–π. –¢—ã —Å–∏–ª—å–Ω–∞—è —Ç—ã —Å —ç—Ç–∏–º —Å–ø—Ä–∞–≤–∏—à—å—Å—è, —è —É–≤–µ—Ä–µ–Ω, –Ω–æ –∫–∞–∫–æ–π —Ü–µ–Ω–æ–π? –ú–Ω–µ –∫–∞–∂–µ—Ç—Å—è, –µ—Å–ª–∏ –ø–æ –∫–∞–∂–¥–æ–º—É –ø—É—Å—Ç—è–∫—É —Ä–∞–∑–¥—É–≤–∞—Ç—å –ø—Ä–æ–±–ª–µ–º—ã, –ø–µ—Ä–µ–∂–∏–≤–∞—Ç—å, –∞ —Å–∞–º–æ–µ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—Ç–∞–ª—å–Ω–æ –≤—ã–≥–æ—Ä–∞—Ç—å, –º–æ–∂–Ω–æ –∏ –≤ –¥—É—Ä–∫—É –ª–µ—á—å.\n–ü–∏–∑–¥–µ—Ü, –∫–∞–∫ —Ç–æ –¥—É—à–Ω–æ —Å—Ç–∞–ª–æ –∫–æ–Ω–µ—á–Ω–æ, –ª–∞–¥–Ω–æ –∫ —á–µ–º—É —ç—Ç–æ —è. –≠—Ç–æ—Ç –∏–¥–∏–æ—Ç –º–Ω–µ –≤—Å—é –ø–ª–µ—à—å –ø—Ä–æ–µ–ª, –æ —Ç–æ–º, —á—Ç–æ —É –≤–∞—Å —Ç—è–∂–µ–ª—ã–π –ø–µ—Ä–∏–æ–¥, —á—Ç–æ –æ–Ω —Å—Ç–∞—Ä–∞–µ—Ç—Å—è –±—ã—Ç—å —Å–∏–ª—å–Ω—ã–º –∞ –æ–Ω–∞ –≥–æ–≤–æ—Ä–∏—Ç —á—Ç–æ —á–µ—Ä—Å—Ç–≤—ã–π –∫–∞–∫ –∫–∞–º–µ–Ω—å, —Ç–∞–∫ –µ—â–µ –∏ –ø–æ—Ö—É–π –Ω–∞ –≤—Å–µ. –ó@e#$% —á–µ—Å—Ç–Ω–æ –≥–æ–≤–æ—Ä—è, –Ω—É –ø—Ä–æ—Ç–∏–≤ —Å–æ–∑–¥–∞—Ç–µ–ª—è –Ω–µ –ø–æ–ø—Ä–µ—à—å, –∫—É–¥–∞ –¥–µ–≤–∞—Ç—å—Å—è.\n–í –æ–±—â–µ–º —Ä–∞–¥–∏ —á–µ–≥–æ –≤—Å–µ —ç—Ç–æ –±—ã–ª–æ, –æ–Ω —Ö–æ—á–µ—Ç —Å–∫–∞–∑–∞—Ç—å —á—Ç–æ –ª—é–±–∏—Ç —Ç–µ–±—è, —Ç—ã –¥–ª—è –Ω–µ–≥–æ –æ—á–µ–Ω—å –≤–∞–∂–Ω–∞, –æ–Ω –∑–Ω–∞–µ—Ç –∫–∞–∫ —Ç–µ–±–µ —Ç—è–∂–µ–ª–æ, –Ω–æ –∫–∞–∫ –µ–º—É –∫–∞–∂–µ—Ç—Å—è —ç—Ç–æ –ø–∞—É–∑–∞ –±—ã–ª–∞ –Ω–µ –æ–±—Ö–æ–¥–∏–º–æ, —á—Ç–æ –±—ã —Ä–∞–∑–æ–±—Ä–∞—Ç—å—Å—è –≤ —Å–∞–º–æ–º —Å–µ–±–µ, –≤ –æ—Ç–Ω–æ—à–µ–Ω–∏—è—Ö, –Ω–µ–º–Ω–æ–≥–æ –æ—Å—Ç—É–¥–∏—Ç—å –≥–æ–ª–æ–≤—É, —á—Ç–æ –±—ã —Ç—ã —Ç–æ–∂–µ –Ω–µ–º–Ω–æ–≥–æ –∫–∞–∫ —Ç–æ –æ—Ç–æ—à–ª–∞, –º–æ–∂–µ—Ç –∑–∞–Ω–æ–≤–æ –ø–µ—Ä–µ–æ—Å–º—ã—Å–ª–∏–ª–∞, —á—Ç–æ-—Ç–æ, –Ω—É –∫—Ä—á –Ω–µ–º–Ω–æ–∂–∫–æ –æ—Ç–¥–æ—Ö–Ω—É—Ç—å –æ—Ç —ç—Ç–æ–≥–æ –¥—É—Ä–∞–∫–∞. –ü–æ—ç—Ç–æ–º—É –ø–æ–∂–∞–ª—É–π—Å—Ç–∞ –Ω–µ –≥—Ä—É—Å—Ç–∏, —Ç—É–ø—ã–µ –∏ –Ω–µ—Å—É—Ä–∞–∑–Ω—ã–µ –º—ã—Å–ª–∏ –∏–∑ –≥–æ–ª–æ–≤—ã –≤—ã–±—Ä–æ—Å—å, –∏ —Å–∞–º–æ–µ –≥–ª–∞–≤–Ω–æ–µ –Ω–µ –ø–µ—Ä–µ–∂–∏–≤–∞–π, –æ–Ω –≤–µ—Ä–∏—Ç —á—Ç–æ –≤—ã –ø—Ä–æ–π–¥–µ—Ç–µ —á–µ—Ä–µ–∑ —ç—Ç–æ.\n–í–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ–æ—Ç, –∫–∞–∂–µ—Ç—Å—è –Ω–∞–∫–æ–Ω–µ—Ü —Ç–æ –≤—Å–µ, —ç—Ç–æ—Ç –∏–¥–∏–æ—Ç –∑–∞—Å—Ç–∞–≤–∏–ª –º–µ–Ω—è—Ç—å –ø–æ–ø–æ—Ç–µ—Ç—å –±—É–¥—å –∑–¥–æ—Ä–æ–≤.\n–ù—É –∏ –ª–∏—á–Ω–æ –æ—Ç –º–µ–Ω—è —Å–æ–≤–µ—Ç, –≤—ã —á–µ –∫–∞–∫ –¥%–±–∏–ª—ã —Ä–µ–∞–ª—å–Ω–æ, –∫–∞–∫ –º–æ–∂–Ω–æ –∏–∑ —Ö–æ—Ä–æ—à–µ–≥–æ –≤—Ä–µ–º—è –ø—Ä–æ–≤–æ–∂–¥–µ–Ω–∏—è –∏–∑-–∑–∞ –∫–∞–∫–æ–π-—Ç–æ —Ö–µ—Ä–Ω–∏ –º–∞–ª–æ —Ç–æ–≥–æ, —á—Ç–æ –≤–µ—Å—å –¥–µ–Ω—å –∏—Å–ø–æ—Ä—Ç–∏—Ç—å —Ç–∞–∫ –µ—â–µ –∏ –∑–∞–≥–Ω—É—Ç—å—Å—è –ø–æ–ø–ª–∞–∫–∞—Ç—å, –∏ –≤–∑—è—Ç—å –ø–∞—É–∑—É. –í—ã –¥–∞–≤–∞–π—Ç–µ –Ω–µ —Ç—É–ø–∏—Ç–µ, –æ—Ç–¥–æ—Ö–Ω–∏—Ç–µ –∏ –ø–æ–≥–æ–≤–æ—Ä–∏—Ç–µ –æ–± —ç—Ç–æ–º. –¢—ã —É–º–Ω–∞—è, –∫—Ä–∞—Å–∏–≤–∞—è, –ø—Ä–µ–∫—Ä–∞—Å–Ω–∞—è –¥–µ–≤—á–µ–Ω–∫–∞. –£–¥–∞—á–∏ –≤–∞–º. –° –Ω–∞–∏–ª—É—á—à–∏–º–∏ –ø–æ–∂–µ–ª–∞–Ω–∏—è–º–∏, –±–æ—Ç")
                sti = open("stik/AnimatedSticker1.tgs", "rb")
                bot.send_sticker(message.chat.id, sti)
            elif message.text == "–ú–¥–∞ —Ç—Ä–µ—à, –ø–æ—à–µ–ª –Ω–∞@%$":
                bot.send_message(message.chat.id,"–ì—Ä—É–±–∞–≤–∞—Ç–æ, –Ω–æ —Å–ø—Ä–∞–≤–µ–¥–ª–∏–≤–æ, –ø—Ä–æ—Å—Ç–∏, —á—Ç–æ –∑–∞–Ω—è–ª —Ç–≤–æ–µ –≤—Ä–µ–º—è. –ï—Å–ª–∏ –∑–∞—Ö–æ—á–µ—à—å, –≤—ã—Å–ª—É—à–∞—Ç—å, —è —Ç—É—Ç –Ω–∏–∫—É–¥–∞ –Ω–µ –¥–µ–Ω—É—Å—å, –ø–æ–∫–µ–¥–∞.")
                sti = open("stik/AnimatedSticker3.tgs", "rb")
                bot.send_sticker(message.chat.id, sti)
            elif message.text == "–†–∞—Å—Å–∫–∞–∂–∏ –º–Ω–µ –æ –Ω–µ–º!":
                bot.send_message(message.chat.id, "–≠—Ç–æ –Ω–µ–æ–ø—ã—Ç–Ω–∞—è —á–º–∏–Ω–∞ –Ω–∏—á–µ–≥–æ –æ —Å–µ–±–µ –Ω–µ —Ä–∞—Å—Å–∫–∞–∑–∞–ª —Ç–æ–ª–∫–æ–º, –¥–∞–ª –∑–∞–¥–∞—á—É –∏ —Å–∫–∞–∑–∞–ª –∏–¥–∏ –∏—Å–ø–æ–ª–Ω—è–π, –Ω–µ –º–æ–≥–ª–∞ –±—ã —Ç—ã —Ä–∞—Å—Å–∫–∞–∑–∞—Ç—å –æ –Ω–µ–º? –Ø —Ç–∞–∫–∂–µ –º–æ–≥—É –≤—ã—Å–ª—à—É–∞—Ç—å —Ç–µ–±—è, –æ—Ç–≤–µ—Ç–∏—Ç—å —Ç–æ–ª–∫–æ–º –Ω–µ —Å–º–æ–≥—É –Ω–æ —Å–ª—É—à–∞—Ç–µ–ª—å —è –æ—Ç–º–µ–Ω–Ω—ã–π.")
            else:
                bot.send_message(message.chat.id, '–Ø —Ç–∞–∫ –∏ –∑–Ω–∞–ª, —á—Ç–æ –æ–Ω –≥–∞–Ω–¥–æ–Ω, —è –µ–º—É —ç—Ç–æ –µ—â–µ —Ä–∞–∑ –ø–µ—Ä–µ–¥–∞–º!!! –†–∞—Å—Å–∫–∞–∂–∏ –µ—â–µ —á—Ç–æ –Ω–∏–±—É–¥—å!')
                sti = open("stik/AnimatedSticker4.tgs", "rb")
                bot.send_sticker(message.chat.id, sti)

    bot.polling(none_stop=True)
if __name__ == "__main__":
    telegram_bot(token)
